CREATE TABLE IF NOT EXISTS `up_activity` (
  `aid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '活动ID',
  `type` char(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '活动类型',
  `title` char(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '活动标题',
  `content` varchar(20000) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '活动内容',
  `starttime` char(20) NOT NULL COMMENT '开始时间',
  `endtime` char(20) NOT NULL COMMENT '结束时间',
  `college` char(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '所在学院',
  `numbers` int(12) unsigned NOT NULL,
  `count` int(12) unsigned NOT NULL,
  `lastmodifytime` char(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '最后修改时间',
  PRIMARY KEY (`aid`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=59 ;

