--
-- 表的结构 `up_inform`
--

CREATE TABLE IF NOT EXISTS `up_inform` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` char(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '通知标题',
  `content` varchar(20000) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '通知内容',
  `type` char(10) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '通知类型',
  `time` char(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '最后修改时间',
  `college` char(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '发布学院',
  `username` char(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '发布者姓名',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=30 ;

--
-- 转存表中的数据 `up_inform`
--

INSERT INTO `up_inform` (`id`, `title`, `content`, `type`, `time`, `college`, `username`) VALUES
(12, '2014迎新活动', '', '校级通知', '2014-09-08', '校级通知', '蒲春伟'),
(13, '关于中秋放假通知', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2014年中秋节于9月6日开始放假，于9月8日结束放假，9月9日正常上课！</p>', '院级通知', '2014-09-08', '计算机学院', '蒲春伟'),
