--

CREATE TABLE IF NOT EXISTS `up_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `uid` int(20) NOT NULL COMMENT '用户ID',
  `username` char(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '用户姓名',
  `password` char(32) NOT NULL DEFAULT '' COMMENT '密码',
  `sex` char(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '性别',
  `type` int(11) NOT NULL DEFAULT '0' COMMENT '类型',
  `phone` bigint(15) unsigned NOT NULL COMMENT '电话',
  `loginip` char(15) NOT NULL DEFAULT '' COMMENT '登录ip',
  `logintime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '登录时间',
  `college` char(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '学院',
  `class` char(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '班级',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=72 ;

